function confirmEnding(str, target) {
  // "Never give up and good luck will find you."
  // -- Falco
  str = str.split('');
  target = target.split('');
  let targetLen = target.length;
  let startLooking = str.length - targetLen;

  for(let i =0; i < targetLen; i++){
      if(str[startLooking] !== target[i]){
        return false;
      }
      startLooking++;
  }
  return true;
}

