function mutation(arr) {
  // to lower case all the strings
  for(let i =0; i < arr.length; i++){
    arr[i] = arr[i].toLowerCase();
  }
  //turn the strings into arrays
  let firstString = arr[0].split('');
  let secondString = arr[1].split('');
  // loop through the second string and looking for matches
  for(let j = 0; j < secondString.length ; j++){
    // if we don't find any occurence of the letters 
    //in the second string in the first we return false
      if(firstString.indexOf(secondString[j]) === -1){
        return false;
      }
  }
  // if we looped through and any of them returned -1 all of the letters are in the first string
  return true;
}

mutation(["HeLlo", "hey"]);
