function repeatStringNumTimes(str, num) {
  // repeat after me
  let repeatedString = [];
  str = str.split('');
  if(num <= 0){
    return "";
  }
  for(let i=0; i<num; i++){
    repeatedString.push(...str);
  }
  repeatedString = repeatedString.join('');
  return repeatedString;
}

repeatStringNumTimes("abc", 3);
