function largestOfFour(arr) {
  // You can do this!
  let largestArray = [];

  arr.forEach((a) =>{
    let largest = a[0];
    // loop through each array looking for the largest
    for(let i =0;i< a.length;i++){
        if(a[i] >= largest){
          largest = a[i];
        }
    } 
    // find the largest and puts into the array of the largest ones
    largestArray.push(largest);
  });
  return largestArray;
}

