function titleCase(str) {
  let word;
  let newString = [];
  // make the string into an array
  str = str.split(' ');
  // loop though each word
  for(let i =0; i < str.length; i++){
    // split the word to make it an array
    word = str[i].split('');
    // change the first letter to its uppercase one
    word[0] = word[0].toUpperCase();
    // make the rest of the word lower case 
    for(let j =1; j < word.length;j++){
        word[j] = word[j].toLowerCase();
    }
    // join the word to make it a string
    word = word.join('');
    newString.push(word);
  }
  newString = newString.join(' ');
  return  newString;
}

titleCase("I'm a little tea pot");
