function truncateString(str, num) {
  // Clear out that junk in your trunk
  if(num < str.length){
    let newString = [];
    str = str.split('');
    for(let i =0;i < num; i++){
        newString[i] = str[i];
    }
    newString.push("...");
    newString = newString.join('');
    return newString;
  }
  return str;
}

