function getIndexToIns(arr, num) {
  // Find my place in this sorted array.
  let i =0;
  // sort the array 
  arr.sort((a,b) => a - b);
  // loop through the array and breaks out when find the first greater value
  for(i =0; i < arr.length; i++){
    if(arr[i] >= num){
      break;
    }
  }
// we return the index because
//it should be right before the first great value
  return i;
}

