function addTogether() {
  // verify number of arguments and whether is a number or not
  if(arguments.length === 1 && isANumber(arguments[0])){
    let value = arguments[0];
    // return a function in case of only one argument
    return function(){
      if(isANumber(arguments[0])){
        return value + arguments[0];
      }
      return undefined;
    }
  }
  if(arguments.length === 2 && isANumber(arguments[0]) && isANumber(arguments[1])){
    // if there are two arguments just sum them up
    let sum = arguments[0] + arguments[1];
    return sum;
  }
  return undefined;
}


function isANumber(num){
  if(typeof num === 'number'){
    return true;
  }
  return false;
}
console.log(addTogether(2)(2));
