function convertHTML(str) {
  // create an object with the occurences I want to replace
  let obj = {
    "&": "&amp;",
    "<": "&lt;",
    ">": "&gt;",
    "\"": "&quot;",
    "'": "&apos;"
  };
  // go through the obj replace if finding the keys in the obj
  for(let key in obj){
    str = str.replace(new RegExp(key,'g'),obj[key]);
  }

  return str;
}

convertHTML("Dolce & Gabbana &");
