function diffArray(arr1, arr2) {
  var newArr = [];
  // Same, same; but different.
  // merge the two arrays
  let mergedArrays = arr1.concat(arr2);
  // verify different element for each array
  for(let i = 0; i < mergedArrays.length; i++){
      if(arr1.indexOf(mergedArrays[i]) === -1){
          newArr.push(mergedArrays[i]);
      }
      if(arr2.indexOf(mergedArrays[i]) === -1){
          newArr.push(mergedArrays[i]);
      }
  }


  return newArr;
}

