function pairElement(str) {
  // TA, GC, AT, CG -- acceptable pairs
  let pairArray = [];
  // turn the  string into an array
  str = str.split('');
  // loop through the string array and match the pairs accordingly
  for(let i = 0; i < str.length; i++){
      switch(str[i]){
        case "G":
          pairArray.push(["G","C"]);
          break;
        case "C":
          pairArray.push(["C","G"]);
          break;
        case "T":
          pairArray.push(["T","A"]);
          break;
        case "A":
          pairArray.push(["A","T"]);
          break;
      }
  }
  return pairArray;
}

