function dropElements(arr, func) {
  // Drop them elements.
  for(let i =0;i < arr.length;i++){
    //removes elements until find the first element
    // that makes func true
    if(func(arr[i])){
      break;
    }else{
      arr.splice(i,1);
      i--;

    }
  }


  return arr;
}

dropElements([1, 2, 3], function(n) {return n < 3; });
