function truthCheck(collection, pre) {
  // Loop through the objs 
  for(let i = 0; i < collection.length; i++){
    // verify if the value read from pre is falsy or truthy
    if(Boolean(collection[i][pre]) === false){
      // one of them being being falsy return false
      return false; 
    }
  }
  // if it looped though and didn't find a falsy value the collecion only contain truthy values, so return true
  return true;
}

