function orbitalPeriod(arr) {
  var GM = 398600.4418;
  var earthRadius = 6367.4447;
  let newArr = [];
  // implement the conversion formula 
  for(let avg in arr){
    let T = Math.round(2*Math.PI* Math.sqrt(Math.pow(earthRadius + arr[avg].avgAlt,3)/GM));
    // create the object with the specifications
    let newObj = {
    name: arr[avg].name,
    orbitalPeriod: T
    };
    // push it into a new array
    newArr.push(newObj);
  }

  return newArr;
}

