function translatePigLatin(str) {
  // get the first vowel
  let firstVowel = str.indexOf(str.match(/[a,e,i,o,u]/));
  // get the initial consonant(s)
  let firstCons = str.substring(0,firstVowel);
  let isVowel = false;
  if(firstVowel === 0){
   isVowel = true;
  }

  str = str.split('');

  // if starts with a vowel add way
  if(isVowel){
    str.push('way');
  }else{
    // if does not start with vowel add ay
    str.splice(0,firstCons.length);
    str.push(firstCons);
    str.push('ay');
  }

  str = str.join('');
  console.log(str);
  return str;
}

translatePigLatin("california");
