function destroyer(arr) {
  // Remove all the values
  for(let i = 0; i < arguments[0].length;i++){
    for(let j =1;j < arguments.length;j++){
      if(arguments[0][i] === arguments[j]){
        arr.splice(i,1);
        i--; // to adjust the i since it's being spliced
      }
    }
  }

  return arr;
}

