function smallestCommons(arr) {
  let newArr = [];
  // sort the passed array
  arr.sort((a,b) => {
    return b - a;
  });
  // create an array with the missing number in the range
  for(let i =arr[0]; i >= arr[1]; i--){
    newArr.push(i);
  }
  // the smallest number can be multipling the two biggest number in the array
  let smallest = newArr[0]*newArr[1];

  while(1){
    let n  = 0;
    // verify if the the smallest is divisible by the two biggest number in the array
    if(smallest%newArr[0]  === 0 && smallest%newArr[1] === 0){
      for(let j = 0; j < newArr.length; j++){
        if(smallest%newArr[j] !==0){
          break;
        }
        n++;
      }
    }
    // it will be look for the number until all numbers in the array are matched
    if(n === newArr.length){
      break
    }
    // increase the number by the biggest number in the array
    smallest += arr[0];
  }

  return smallest;
}


