function steamrollArray(arr) {
  // I'm a steamroller, baby
  let newArr =[];
  // we loop through the array of arrays and verify if it's an array
  for(let i = 0; i < arr.length; i++){
    // if so we grab the content within and put into the newArr
    if(Array.isArray(arr[i])){
      for(let j  =0; j < arr[i].length; j++){
        newArr.push(findContent(arr[i][j]));
      }       
    }
    else{
      // else we just need to put into the newArr
      newArr.push(arr[i]);
    }
  }
  console.log(newArr);
 return newArr; 
}

// this function find the content in the nested array
function findContent(a){
  while(Array.isArray(a)){
    a = a[0];
  }
  console.log(a);
  return a;
}
