function sumFibs(num) {
  let fibArray = [];
  let sum =0;
  // calculate the fibbonacci number 
  for(let i = 0; i <= num; i++){
    if(i < 2){
      fibArray[i] = 1;
    }
    else{
      fibArray[i] = fibArray[i-1] + fibArray[i-2];
    }
    // only sum them if they are odd and less than the given number
    if(fibArray[i] % 2 !== 0 && fibArray[i] <= num){
          sum += fibArray[i];
    }

  }
  return sum;
}

sumFibs(4);
