function sumPrimes(num) {
  let sum =0;
  let arr =[];
  // create an array with all the values up to num
  for(let i =2;i<=num;i++){
     let obj ={
      num: i,
      mark:0
    };
    arr.push(obj);
  }
  // implement my version Sieve of Eratosthenes
  for(let j =2;j <= num;j++){
    for(let i =0;i < arr.length; i++){
      if(arr[i].num %j === 0 && arr[i].num !== j){
        arr[i].mark = 1;
      }
    }
  }
  // sum the array of prime numbers
  for(let k =0;k < arr.length;k++){
    if(arr[k].mark === 0){
      sum += arr[k].num;
      console.log(arr[k].num);
    }
  }

  return sum;
}

