function rot13(str) { // LBH QVQ VG!
  // split the string to manipulate it
  let arrStr = str.split('');

  // 65 - 90 A-Z ASCII code
  for(let i =0; i < arrStr.length; i++){
    // grabbed the ASCII code from each character
    let code = arrStr[i].charCodeAt(0);
    // only shifted the values if they were uppercase letters
    if(code >= 65 && code <=90){
      //since the shift is 13 we sum it to the ASCII code
      let sum = code + 13;
      // we must care for the sum to be within the letters range
      if(sum > 90){
        let shift = sum - 90;
        sum = 64 + shift; 
      }
      // we have to replace to the respected shifted value 
      arrStr[i] = String.fromCharCode(sum);
    }
  }
  // we join to make into a string again and return it
  arrStr = arrStr.join('');
  return arrStr;
}

