function palindrome(str) {
  // Good luck!
  // removel all non alphanumerica caracters
  let pattern = /[\W ||_]/g;
  let newStr = str.split(pattern);
  newStr = newStr.join('').toUpperCase();

  console.log(newStr);
  //check the length of the str
  if(newStr.length%2 ===0){
    // if the length is even we have to check the middle right and left
    let middleLeft = newStr.length/2 -1;
    let middleRight = newStr.length/2;
    // loop through the string and if all characters are equal in the two halves it is indeed a palindrome else it's not
    for(let i = 0; i < newStr.length/2; i++){
      if(newStr[middleLeft-i] !== newStr[middleRight+i]){
        return false;
      }
    }
    return true;
  }else{
    // it's the same thing as the above one but we do have a middle so we don't need to worry about middle left and right
     let middle = Math.floor(newStr.length/2);
      for(let i = 0; i < newStr.length/2; i++){
        if(newStr[middle-i] !== newStr[middle+i]){
          return false;
        }
    }
  }
  return true;
}


console.log(palindrome("2A3*3a2"));
palindrome("2_A3*3#A2");
