function convertToRoman(num) {
    // create the object which contains the roman simbols
    const decimals = [1,4,5,9,10,40,50,90,100,400,500,900,1000];
    const romans = ["I","IV" ,"V", "IX","X","XL","L","XC","C","CD","D","CM", "M"];
    let arrNums = [];

    // extract the algorithms
    while(num >= 1){
        arrNums.push(num%10);
        num = Math.floor(num/10);
    }

    // multiply by a factor to find the real number
    let factor =1;
    for(let i  =0;i < arrNums.length;i++){
        arrNums[i] = arrNums[i]*factor;
        factor*=10;
    }

    // sort descendendly
    arrNums.sort((a,b)=>{
        return b-a;
    })

    // filter the zeros out
    arrNums = arrNums.filter((a)=> { return a  > 0 });
    console.log(arrNums);

    // convert each number in the array to its roman numeral equivalent
    let romanEquivalent ='';
    for(let j = 0;j < arrNums.length; j++){
            // get a roman equivalent to each number in the array and then we concatenate them all into the final number
       romanEquivalent += getRoman(romans,decimals,arrNums,j);
    }
    console.log(romanEquivalent);

    return romanEquivalent;
}

