function telephoneCheck(str) {
  // Good luck!
  // create the acceptable patterns
  let pattern1 = /\b(^\d{3}[-]?\d{3}[-]?\d{4})\b/g;
  let pattern2 = /^(\(\d{3}\)(\s)*\d{3}[-]\d{4})\b/g;
  let pattern3 = /^([1]{1}(\s)*\d{3}([-,\s])*\d{3}([-,\s])*\d{4})\b/g;
  let pattern4 = /^([1]{1}(\s)*\(\d{3}\)([-,\s])*\d{3}([-,\s])*\d{4})\b/g;

  // if it checks to an acceptable pattern returns true
  if(str.match(pattern1) !== null){
    return true;
  }
  if(str.match(pattern2) !== null){
    return true;
  }
  if(str.match(pattern3) !== null){
    return true;
  }
  if(str.match(pattern4) !== null){
    return true;
  }
  // else return false meaning it's not a valid US number
  return false;
}

telephoneCheck("1 555)555-5555");
