function fiboEvenSum(n) {
  // You can do it!
  const fibArr =[1,2];
  let sumFibEven =0;
  // generate the fibonacci sequence and put it into fibArr
  for(let i =0;i < n - 1;i++){
      let sumFibElem = fibArr[i] + fibArr[i+1];
      fibArr.push(sumFibElem);
  }
  // verify and sum the even numbers in the fibArr
  for(let i =0;i < fibArr.length ;i++){
      if(fibArr[i]%2 === 0){
        sumFibEven+= fibArr[i];
      }
  }

  return sumFibEven;
}

fiboEvenSum(10);
